import { useEffect, useState } from "react";
import { Card, Button } from "react-bootstrap";

const promozioni = () => {
  const [promozione, setPromozione] = useState(null);

  useEffect(() => {
    fetch("http://localhost:9090/promozioni")
      .then((response) => response.json())
      .then((data) => setPromozione(data))
      .catch((error) => console.error("Errore nel recupero dei dati:", error));
  }, []);

  const acquistaPromozione = () => {
    if (!promozione) return;

    fetch("http://localhost:9090/acquista", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ id: promozione.id }),
    })
      .then((response) => response.json())
      .then((data) => alert("Promozione acquistata con successo!"))
      .catch((error) => console.error("Errore nell'acquisto:", error));
  };

  return (
    <Card className="text-center">
      <Card.Header>Offerta Speciale</Card.Header>
      {promozione ? (
        <>
          <Card.Body>
            <Card.Title>{promozione.nome}</Card.Title>
            <Card.Text>{promozione.descrizione}</Card.Text>
            <p>
              <strong>Giga:</strong> {promozione.giga} | <strong>Minuti:</strong> {promozione.minuti} | <strong>Messaggi:</strong> {promozione.messaggi}
            </p>
            <p>
              <strong>Prezzo:</strong> â‚¬{promozione.prezzo}
            </p>
            <Button variant="primary" onClick={acquistaPromozione}>
              Acquista
            </Button>
          </Card.Body>
          <Card.Footer className="text-muted">Disponibile ora</Card.Footer>
        </>
      ) : (
        <Card.Body>Caricamento...</Card.Body>
      )}
    </Card>
  );
};

export default promozioni;
