import React, { useState, useEffect } from "react";
import { Button, Container, Row, Col, Form, Alert } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

const pagamento = () => {
  const navigate = useNavigate();
  const [paymentSuccess, setPaymentSuccess] = useState(false);
  const [paymentData, setPaymentData] = useState(null);

  const handlePayment = async () => {
    // Simula un pagamento
    try {
      // Qui si può integrare il gateway di pagamento (Stripe, PayPal, ecc)
      // Per esempio, simuliamo il pagamento come riuscito:
      const paymentResponse = {
        success: true,
        amount: 100, // Quantità pagata (in questo caso, per esempio 100)
        promotion: "Promo 50% sconto", // Dettaglio della promozione
      };

      // Se il pagamento è riuscito, aggiornare lo stato
      if (paymentResponse.success) {
        setPaymentSuccess(true);
        setPaymentData(paymentResponse);

        // Simula l'aggiornamento dei dati utente
        const token = localStorage.getItem("authToken");
        if (token) {
          const response = await fetch("http://localhost:9090/api/auth/user/update-promotion", {
            method: "PUT",
            headers: {
              Authorization: `Bearer ${token}`,
              "Content-Type": "application/json",
            },
            body: JSON.stringify({
              promotion: paymentResponse.promotion,
            }),
          });

          if (!response.ok) {
            throw new Error("Errore nell'aggiornamento della promozione");
          }
        }
      }
    } catch (error) {
      console.error("Errore nel pagamento", error);
    }
  };

  return (
    <Container>
      <Row>
        <Col>
          <h1>Pagina di Acquisto</h1>
          <h2>Dettagli Promozione</h2>
          <p>Promozione disponibile: 50% di sconto su tutti i piani</p>

          <Form>
            <Form.Group controlId="formPayment">
              <Form.Label>Metodo di pagamento</Form.Label>
              <Form.Control type="text" placeholder="Inserisci i dettagli del pagamento" />
            </Form.Group>
            <Button variant="primary" onClick={handlePayment}>
              Completa il pagamento
            </Button>
          </Form>

          {paymentSuccess && (
            <Alert variant="success" className="mt-3">
              <h3>Pagamento effettuato con successo!</h3>
              <p>Promozione: {paymentData?.promotion}</p>
            </Alert>
          )}

          {!paymentSuccess && (
            <Alert variant="danger" className="mt-3">
              Errore nel pagamento. Riprova.
            </Alert>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default pagamento;
