import React, { useState, useEffect } from "react";
import { Button, Container, Row, Col, Form, Alert } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

const Pagamento = () => {
  const navigate = useNavigate();
  const [paymentSuccess, setPaymentSuccess] = useState(false);
  const [paymentData, setPaymentData] = useState(null);
  const [promotionId, setPromotionId] = useState(""); // Stato per memorizzare la promozione selezionata
  const [userId, setUserId] = useState(""); // Stato per memorizzare l'ID dell'utente

  // Funzione per recuperare l'ID dell'utente dal token
  const getUserIdFromToken = () => {
    const token = localStorage.getItem("authToken");
    if (token) {
    }
    return null;
  };

  // Usa `useEffect` per recuperare dinamicamente l'ID utente al caricamento
  useEffect(() => {
    const userIdFromToken = getUserIdFromToken();
    if (userIdFromToken) {
      setUserId(userIdFromToken); // Memorizza l'ID utente
    }
    console.log("ID utente recuperato:", userIdFromToken);
  }, []);

  const handlePayment = async () => {
    try {
      if (!promotionId || !userId) {
        throw new Error("ID promozione o ID utente non validi");
      }

      if (paymentResponse.success) {
        setPaymentSuccess(true);
        setPaymentData(paymentResponse);

        const token = localStorage.getItem("authToken");
        if (token) {
          const response = await fetch(`http://localhost:9090/api/auth/user/${paymentResponse.userId}/promozioni/${paymentResponse.promotionId}`, {
            method: "POST",
            headers: {
              Authorization: `Bearer ${token}`,
              "Content-Type": "application/json",
            },
          });

          const responseData = await response.json();
          if (!response.ok) {
            throw new Error(responseData.error || "Errore nell'aggiornamento della promozione");
          }

          console.log("Promozione aggiornata:", responseData);
        }
      }
    } catch (error) {
      console.error("Errore nel pagamento o nell'aggiornamento della promozione", error);
    }
  };

  const paymentResponse = {
    success: true,
    amount: 100,
    promotionId: promotionId,
    userId: userId,
  };
  return (
    <Container>
      <Row>
        <Col>
          <h1>Pagina di Acquisto</h1>
          <h2>Dettagli Promozione</h2>

          <Form>
            <Form.Group controlId="formPayment">
              <Form.Label>Metodo di pagamento</Form.Label>
              <Form.Control type="text" placeholder="Inserisci i dettagli del pagamento" />
            </Form.Group>

            <Form.Group controlId="formPromotions">
              <Form.Label>Seleziona una promozione</Form.Label>
              <Form.Control as="select" value={promotionId} onChange={(e) => setPromotionId(e.target.value)}>
                <option value="">Scegli una promozione</option>
                <option value="promo123"></option>
                <option value="promo456">Promozione 2</option>
                <option value="promo789">Promozione 3</option>
              </Form.Control>
            </Form.Group>

            <Button variant="primary" onClick={handlePayment}>
              Completa il pagamento
            </Button>
          </Form>

          {paymentSuccess && (
            <Alert variant="success" className="mt-3">
              <h3>Pagamento effettuato con successo!</h3>
            </Alert>
          )}

          {!paymentSuccess && (
            <Alert variant="danger" className="mt-3">
              Errore nel pagamento. Riprova.
            </Alert>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default Pagamento;
