import { useEffect, useState } from "react";
import { Card, Button } from "react-bootstrap";

const Promozioni = () => {
  const [promozione, setPromozione] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("http://localhost:9090/promozioni")
      .then((response) => response.json())
      .then((data) => {
        setPromozione(data);
        setLoading(false);
        console.log(data);
      })
      .catch((error) => {
        console.error("Errore nel recupero dei dati:", error);
        setLoading(false);
      });
  }, []);

  return (
    <Card className="text-center">
      <Card.Header>Offerta Speciale</Card.Header>
      {loading ? (
        <Card.Body>Caricamento...</Card.Body>
      ) : promozione ? (
        <>
          <Card.Body>
            <Card.Title>{promozione.nome}</Card.Title>
            <Card.Text>{promozione.descrizione}</Card.Text>
            <p>
              <strong>Giga:</strong> {promozione.giga} | <strong>Minuti:</strong> {promozione.minuti} | <strong>Messaggi:</strong> {promozione.messaggi}
            </p>
            <p>
              <strong>Prezzo:</strong> â‚¬{promozione.prezzo}
            </p>
            <Button variant="primary">Acquista</Button>
          </Card.Body>
          <Card.Footer className="text-muted">Disponibile ora</Card.Footer>
        </>
      ) : (
        <Card.Body>Errore nel caricamento dei dati.</Card.Body>
      )}
    </Card>
  );
};

export default Promozioni;
