import React, { useState, useEffect } from "react";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";

function Telefoni() {
  const [telefono, setTelefono] = useState(null);

  // Esegui la fetch al montaggio del componente
  useEffect(() => {
    fetch("http://localhost:9090/telefoni")
      .then((response) => response.json())
      .then((data) => {
        setTelefono(data); // Imposta il dato ricevuto nello stato
      })
      .catch((error) => {
        console.error("Errore nella fetch:", error);
      });
  }, []); // [] assicura che la fetch venga eseguita solo una volta al montaggio

  // Se i dati non sono ancora caricati, mostriamo un messaggio di caricamento
  if (!telefono) {
    return <p>Caricamento...</p>;
  }

  return (
    <Card style={{ width: "18rem" }}>
      <Card.Img variant="top" src={telefono.imgUrl} alt={`Immagine di ${telefono.modello}`} />
      <Card.Body>
        <Card.Title>{telefono.modello}</Card.Title>
        <Card.Text>
          <strong>Marca:</strong> {telefono.marca}
        </Card.Text>
        <Card.Text>
          <strong>Numero di Serie:</strong> {telefono.numeroSeriale}
        </Card.Text>
        <Card.Text>
          <strong>Memoria:</strong> {telefono.memoria} GB
        </Card.Text>
        <Button variant="primary">Go somewhere</Button>
      </Card.Body>
    </Card>
  );
}

export default Telefoni;
