import React, { useEffect, useState } from "react";
import { ListGroup, Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

const Profilo = () => {
  const [user, setUser] = useState({ firstName: "" });

  useEffect(() => {
    const fetchUserData = async () => {
      const token = localStorage.getItem("authToken");

      if (!token) {
        console.error("Token non trovato");
        return;
      }

      try {
        const response = await fetch("http://localhost:9090/api/auth/user", {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });

        if (!response.ok) {
          throw new Error(`Errore HTTP: ${response.status}`);
        }

        const data = await response.json();
        setUser({ firstName: data.firstName });
      } catch (error) {
        console.error("Errore nel recupero dei dati utente", error);
      }
    };

    fetchUserData();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("authToken");
    navigate("/login");
  };

  return (
    <>
      <Button variant="primary">Primary</Button>
      <h1 className="text-center my-3 fw-bolder">Benvenuto , {user.firstName}</h1>
      <ListGroup>
        <ListGroup.Item disabled>{user.lastName}</ListGroup.Item>
        <ListGroup.Item>Dapibus ac facilisis in</ListGroup.Item>
        <ListGroup.Item>Morbi leo risus</ListGroup.Item>
        <ListGroup.Item>Porta ac consectetur ac</ListGroup.Item>
      </ListGroup>
    </>
  );
};

export default Profilo;
