import React, { useEffect, useState } from "react";
import { FloatingLabel, Form } from "react-bootstrap";

const Profilo = () => {
  const [user, setUser] = useState({
    firstName: "",
    lastName: "",
    email: "",
    username: "",
    password: "",
    telefono: "",
    telefonoLineaFissa: "",
    indirizzo: "",
    cap: "",
    citta: "",
  });

  useEffect(() => {
    const fetchUserData = async () => {
      const token = localStorage.getItem("token");
      if (!token) {
        console.error("Token non trovato");
        return;
      }

      try {
        const response = await fetch("http://localhost:9090/api/auth/user", {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });

        if (!response.ok) {
          throw new Error(`Errore HTTP: ${response.status}`);
        }

        const data = await response.json();
        setUser(data);
      } catch (error) {
        console.error("Errore nel recupero dei dati utente", error);
      }
    };

    fetchUserData();
  }, []);

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  return (
    <div>
      <FloatingLabel className="mb-3" label="Nome">
        <Form.Control type="text" name="firstName" value={user.firstName} onChange={handleChange} required />
      </FloatingLabel>
      <FloatingLabel className="mb-3" label="Cognome">
        <Form.Control type="text" name="lastName" value={user.lastName} onChange={handleChange} required />
      </FloatingLabel>
      <FloatingLabel className="mb-3" label="Email">
        <Form.Control type="email" name="email" value={user.email} onChange={handleChange} required />
      </FloatingLabel>
      <FloatingLabel className="mb-3" label="Username">
        <Form.Control type="text" name="username" value={user.username} onChange={handleChange} required />
      </FloatingLabel>
      <FloatingLabel className="mb-3" label="Password">
        <Form.Control type="password" name="password" value={user.password} onChange={handleChange} required />
      </FloatingLabel>
      <FloatingLabel className="mb-3" label="Telefono Linea Fissa">
        <Form.Control type="text" name="telefonoLineaFissa" value={user.telefonoLineaFissa} onChange={handleChange} required />
      </FloatingLabel>
      <FloatingLabel className="mb-3" label="CAP">
        <Form.Control type="text" name="cap" value={user.cap} onChange={handleChange} required />
      </FloatingLabel>
      <FloatingLabel className="mb-3" label="Telefono">
        <Form.Control type="text" name="telefono" value={user.telefono} onChange={handleChange} required />
      </FloatingLabel>
      <FloatingLabel className="mb-3" label="Indirizzo">
        <Form.Control type="text" name="indirizzo" value={user.indirizzo} onChange={handleChange} required />
      </FloatingLabel>
      <FloatingLabel className="mb-3" label="Città">
        <Form.Control type="text" name="citta" value={user.citta} onChange={handleChange} required />
      </FloatingLabel>
    </div>
  );
};

export default Profilo;
