import { useEffect, useState } from "react";
import { Card, Button } from "react-bootstrap";

const Promozioni = () => {
  const [promozioni, setPromozioni] = useState([]); // Changed to an array
  const [loading, setLoading] = useState(true);

  const fetchData = async () => {
    const token = localStorage.getItem("authToken");

    if (!token) {
      console.error("nessun token");
      return;
    }
  };
  useEffect(() => {
    const token =
      "eyJhbGciOiJIUzI1NiJ9.eyJzdWIiOiJzdHJpbmciLCJyb2xlcyI6WyJST0xFX1VTRVIiXSwiaWF0IjoxNzQxNzg3NDUyLCJleHAiOjE3NDE3OTYwOTJ9.F416CFg6YGI_5Zgd_9jGzBN99StyEZuxCBJEpl4-Fsk";

    fetch("http://localhost:9090/promozioni", {
      headers: {
        Authorization: `Bearer ${token}`,
        Accept: "application/json",
      },
    })
      .then((response) => response.json())
      .then((data) => {
        setPromozioni(data); // Set the data as an array of promotions
        setLoading(false);
        console.log(data);
      })
      .catch((error) => {
        console.error("Errore nel recupero dei dati:", error);
        setLoading(false);
      });
  }, []);

  return (
    <div>
      {loading ? (
        <p>Caricamento...</p>
      ) : promozioni.length > 0 ? (
        promozioni.map((promozione, index) => (
          <Card key={index} className="text-center mt-5" style={{ marginBottom: "1rem" }}>
            <Card.Header className="fw-bold fs-3">{promozione.nome}</Card.Header>
            <Card.Body>
              <Card.Text>{promozione.descrizione}</Card.Text>
              <p>
                <strong>Giga:</strong> {promozione.giga} | <strong>Minuti:</strong> {promozione.minuti} | <strong>Messaggi:</strong> {promozione.messaggi}
              </p>
              <p>
                <strong>Prezzo:</strong> â‚¬{promozione.prezzo}
              </p>
              <Button variant="primary">Acquista</Button>
            </Card.Body>
            <Card.Footer className="text-muted bg-warning">Disponibile ora</Card.Footer>
          </Card>
        ))
      ) : (
        <p>Errore nel caricamento dei dati.</p>
      )}
    </div>
  );
};

export default Promozioni;
