import { useEffect, useState } from "react";
import { Card, Button, Alert } from "react-bootstrap";

const PromozioniLineaFissa = () => {
  const [promozioni, setPromozioni] = useState([]); // Cambiato da `null` a array vuoto
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem("authToken");

    if (!token) {
      console.error("Nessun token trovato");
      setLoading(false);
      return;
    }

    fetch("http://localhost:9090/promozioniLineaFissa", {
      headers: {
        Authorization: `Bearer ${token}`,
        Accept: "application/json",
      },
    })
      .then((response) => response.json())
      .then((data) => {
        setPromozioni(data); // Supponiamo che `data` sia un array
        setLoading(false);
      })
      .catch((error) => {
        console.error("Errore nel recupero dei dati:", error);
        setLoading(false);
      });
  }, []);

  return (
    <>
      <div className="container">
        {loading ? (
          <p className="text-center">Caricamento...</p>
        ) : promozioni.length > 0 ? ( // Controllo se ci sono promozioni
          <div className="row justify-content-center">
            {promozioni.map((promo, index) => (
              <Card key={index} className="text-center my-3 mx-3 shadow-lg" style={{ width: "22rem" }}>
                <Card.Header className="fw-bold fs-4">{promo.nome}</Card.Header>
                <Card.Body>
                  <Card.Text>{promo.descrizione}</Card.Text>
                  <p>
                    <strong>Giga:</strong> {promo.giga} | <strong>Minuti:</strong> {promo.minuti}
                  </p>
                  <p>
                    <strong>Prezzo:</strong> â‚¬{promo.prezzo}
                  </p>
                  <Button variant="primary">Acquista</Button>
                </Card.Body>
                <Card.Footer className="text-muted bg-warning">{promo.promozioniLineaFissaEnum}</Card.Footer>
              </Card>
            ))}
          </div>
        ) : (
          <p className="text-center">Nessuna promozione disponibile.</p>
        )}
      </div>
    </>
  );
};

export default PromozioniLineaFissa;
