import React, { useState } from "react";
import { Button, Container, Row, Col, Form, Alert } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

const Pagamento = () => {
  const navigate = useNavigate();
  const [paymentSuccess, setPaymentSuccess] = useState(false);
  const [paymentData, setPaymentData] = useState(null);

  const handlePayment = async () => {
    try {
      const paymentResponse = {
        success: true,
        amount: 100,
        promotionId: "promo123", // Supponiamo che questo ID venga passato dalla selezione della promozione
        userId: "12345", // Questo dovrebbe essere recuperato dal token o dallo stato dell'utente autenticato
      };

      if (paymentResponse.success) {
        setPaymentSuccess(true);
        setPaymentData(paymentResponse);

        const token = localStorage.getItem("authToken");
        if (token) {
          // Modifica l'URL per utilizzare l'endpoint corretto
          const response = await fetch(`http://localhost:9090/api/auth/user/${paymentResponse.userId}/promozioni/${paymentResponse.promotionId}`, {
            method: "POST",
            headers: {
              Authorization: `Bearer ${token}`,
              "Content-Type": "application/json",
            },
          });

          const responseData = await response.json();
          if (!response.ok) {
            throw new Error(responseData.error || "Errore nell'aggiornamento della promozione");
          }

          console.log("Promozione aggiornata:", responseData); // Verifica la risposta
        }
      }
    } catch (error) {
      console.error("Errore nel pagamento o nell'aggiornamento della promozione", error);
    }
  };

  return (
    <Container>
      <Row>
        <Col>
          <h1>Pagina di Acquisto</h1>
          <h2>Dettagli Promozione</h2>

          <Form>
            <Form.Group controlId="formPayment">
              <Form.Label>Metodo di pagamento</Form.Label>
              <Form.Control type="text" placeholder="Inserisci i dettagli del pagamento" />
            </Form.Group>
            <Button variant="primary" onClick={handlePayment}>
              Completa il pagamento
            </Button>
          </Form>

          {paymentSuccess && (
            <Alert variant="success" className="mt-3">
              <h3>Pagamento effettuato con successo!</h3>
              <p>Promozione: {paymentData?.promotionId}</p> {/* Mostra l'ID della promozione */}
            </Alert>
          )}

          {!paymentSuccess && (
            <Alert variant="danger" className="mt-3">
              Errore nel pagamento. Riprova.
            </Alert>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default Pagamento;
