import { useState, useRef, useEffect } from "react";

const ChatBot = () => {
  const [messages, setMessages] = useState([{ text: "Ciao! Sono FlyBot, come posso aiutarti?", sender: "bot" }]);
  const [inputValue, setInputValue] = useState("");
  const messagesEndRef = useRef(null);

  // Risposte predefinite del bot
  const botResponses = {
    ciao: "Ciao! Come posso aiutarti oggi?",
    salve: "Salve! Dimmi pure come posso esserti utile.",
    aiuto: "Posso aiutarti con informazioni su: prodotti, promozioni o assistenza.",
    promozioni: "Attualmente abbiamo promozioni su linee fisse e telefoni!",
    grazie: "Di niente! Se hai altre domande sono qui.",
    default: "Non ho capito, puoi riformulare?",
  };

  // Scroll automatico alla fine dei messaggi
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!inputValue.trim()) return;

    // Aggiungi messaggio utente
    const userMessage = { text: inputValue, sender: "user" };
    setMessages((prev) => [...prev, userMessage]);
    setInputValue("");

    // Simula ritardo risposta bot
    setTimeout(() => {
      const userText = inputValue.toLowerCase();
      let botResponse = botResponses.default;

      // Cerca una risposta corrispondente
      for (const [keyword, response] of Object.entries(botResponses)) {
        if (userText.includes(keyword)) {
          botResponse = response;
          break;
        }
      }

      setMessages((prev) => [...prev, { text: botResponse, sender: "bot" }]);
    }, 800);
  };

  return (
    <div style={styles.chatContainer}>
      <div style={styles.header}>
        <h3>FlyBot Assistenza</h3>
      </div>

      <div style={styles.messagesContainer}>
        {messages.map((msg, index) => (
          <div
            key={index}
            style={{
              ...styles.message,
              ...(msg.sender === "bot" ? styles.botMessage : styles.userMessage),
            }}
          >
            {msg.text}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <form onSubmit={handleSendMessage} style={styles.inputForm}>
        <input type="text" value={inputValue} onChange={(e) => setInputValue(e.target.value)} placeholder="Scrivi un messaggio..." style={styles.inputField} />
        <button type="submit" style={styles.sendButton}>
          Invia
        </button>
      </form>
    </div>
  );
};
