import React, { useEffect, useState } from "react";
import { ListGroup, Button, Container, Row, Col } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

const Profilo = () => {
  const [user, setUser] = useState({
    firstName: "",
    lastName: "",
    email: "",
    telefono: "",
    telefonoLineaFissa: "",
    indirizzo: "",
    cap: "",
    avatar: null,
    città: "",
  });

  useEffect(() => {
    const fetchUserData = async () => {
      const token = localStorage.getItem("authToken");

      if (!token) {
        console.error("Token non trovato");
        return;
      }

      try {
        const response = await fetch("http://localhost:9090/api/auth/user", {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });

        if (!response.ok) {
          throw new Error(`Errore HTTP: ${response.status}`);
        }

        const data = await response.json();
        setUser({
          firstName: data.firstName,
          lastName: data.lastName,
          email: data.email,
          telefono: data.telefono,
          telefonoLineaFissa: data.telefonoLineaFissa,
          indirizzo: data.indirizzo,
          cap: data.cap,
          avatar: data.avatar,
          città: data.città,
        });
      } catch (error) {
        console.error("Errore nel recupero dei dati utente", error);
      }
    };

    fetchUserData();
  }, []);

  const navigate = useNavigate();
  const handleLogout = () => {
    localStorage.removeItem("authToken");
    navigate("/login");
  };

  return (
    <>
      <Container className="mt-3">
        <Row>
          <Col>
            <h1 className="text-center my-3 fw-bolder">Benvenuto , {user.firstName}</h1>
            <ListGroup>
              <ListGroup.Item>
                <strong>Nome: </strong> {user.firstName}
              </ListGroup.Item>
              <ListGroup.Item>
                <strong>Cognome: </strong> {user.lastName}
              </ListGroup.Item>
              <ListGroup.Item>
                <strong>Email: </strong> {user.email}
              </ListGroup.Item>
              <ListGroup.Item>
                <strong>Telefono: </strong> {user.telefono}
              </ListGroup.Item>
              <ListGroup.Item>
                <strong>Telefono Linea Fissa: </strong> {user.telefonoLineaFissa}
              </ListGroup.Item>
              <ListGroup.Item>
                <strong>Indirizzo: </strong> {user.indirizzo} <strong>CAP: </strong> {user.cap} <strong>Città: </strong> {user.città}
              </ListGroup.Item>
            </ListGroup>
          </Col>
          <Col>
            <img src={user.avatar} alt="Avatar" style={{ width: "200px", height: "200px", borderRadius: "50%" }} />
          </Col>
        </Row>
      </Container>
      <Button variant="danger" onClick={handleLogout}>
        Logout
      </Button>
    </>
  );
};

export default Profilo;
