import React, { useState, useEffect } from "react";
import { FloatingLabel, Form } from "react-bootstrap";
import { Link } from "react-router-dom";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    let authentication = null;
    try {
      authentication = await retriveToken(username, password);
      console.log("Authentication: ", authentication);

      // Se l'autenticazione è andata a buon fine, reindirizzo alla pagina /clients
      if (authentication) {
        navigate("/clients");
      }

      //... altrimenti lancio un errore
    } catch (err) {
      console.error("Error fetching token:", err);
      setError("Credenziali errate. Riprova.");
    }
  };

  useEffect(() => {
    if (AuthHandler() === true) {
      navigate("/clients");
    }
  }, [navigate]);

  return (
    <div className="d-flex justify-content-center align-items-center vh-100">
      <div className="p-5 m-5">
        <h2 className="text-center">Login</h2>
        {error && <div className="alert alert-danger">{error}</div>}
        <form onSubmit={handleSubmit}>
          <FloatingLabel className="mb-3" controlId="floatingUsername" label="Username">
            <Form.Control
              type="text"
              name="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Inserisci il tuo username"
              required
            />
          </FloatingLabel>
          <FloatingLabel className="mb-3" controlId="floatingPassword" label="Password">
            <Form.Control
              type="password"
              name="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Inserisci la tua password"
              required
            />
          </FloatingLabel>
          <button type="submit" className="btn btn-primary w-100">
            Accedi
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
