import React, { useState, useEffect } from "react";
import { FloatingLabel, Form } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const [error, setError] = useState("");

  // Funzione fittizia per ottenere il token
  const retrieveToken = async (username, password) => {
    return new Promise((resolve, reject) => {
      setTimeout(() => {
        if (username === "admin" && password === "1234") {
          resolve("TOKEN12345");
        } else {
          reject("Credenziali errate");
        }
      }, 1000);
    });
  };

  const AuthHandler = (token) => {
    localStorage.setItem("authToken", token);
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const token = await retrieveToken(username, password);
      console.log("Token ricevuto:", token);

      if (token) {
        AuthHandler(token);
        navigate("/");
      }
    } catch (err) {
      console.error("Errore durante il login:", err);
      setError("Credenziali errate. Riprova.");
    }
  };
  const handleLogout = () => {
    localStorage.removeItem("authToken");
    navigate("/login");
  };

  useEffect(() => {
    if (localStorage.getItem("authToken")) {
      navigate("/");
    }
  }, [navigate]);

  return (
    <div className="d-flex justify-content-center align-items-center vh-50">
      <div>
        <h2 className="text-center fs-1 fw-bold">Login</h2>
        {error && <div className="alert alert-danger">{error}</div>}
        <form onSubmit={handleSubmit}>
          <FloatingLabel className="mb-3" controlId="floatingUsername" label="Username">
            <Form.Control
              type="text"
              name="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Inserisci il tuo username"
              required
            />
          </FloatingLabel>
          <FloatingLabel className="mb-3" controlId="floatingPassword" label="Password">
            <Form.Control
              type="password"
              name="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Inserisci la tua password"
              required
            />
          </FloatingLabel>
          <button type="submit" className="btn btn-warning w-100">
            Accedi
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
