import { useState } from "react";
import { FloatingLabel, Form } from "react-bootstrap";

const Registrati = () => {
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [nome, setNome] = useState("");
  const [cognome, setCognome] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [username, setUsername] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!username || !password || !nome || !cognome || !email) {
      setError("Tutti i campi sono obbligatori.");
      return;
    }

    setLoading(true);
    try {
      const response = await fetch("http://localhost:9090/api/auth/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          nome,
          cognome,
          email,
          password,
          username,
        }),
      });

      if (!response.ok) {
        throw new Error("Errore nella registrazione, riprova.");
      }

      const data = await response.json();
      console.log("Registrazione avvenuta con successo", data);

      setNome("");
      setCognome("");
      setEmail("");
      setPassword("");
      setUsername("");
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="d-flex justify-content-center vh-100 mt-5">
      <div>
        <h2 className="text-center fs-1 fw-bold">Registrati</h2>
        {error && <div className="alert alert-danger">{error}</div>}
        <form onSubmit={handleSubmit}>
          <FloatingLabel className="mb-3" label="Nome">
            <Form.Control type="text" value={nome} onChange={(e) => setNome(e.target.value)} required />
          </FloatingLabel>
          <FloatingLabel className="mb-3" label="Cognome">
            <Form.Control type="text" value={cognome} onChange={(e) => setCognome(e.target.value)} required />
          </FloatingLabel>
          <FloatingLabel className="mb-3" label="Email">
            <Form.Control type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
          </FloatingLabel>
          <FloatingLabel className="mb-3" label="Username">
            <Form.Control type="text" value={username} onChange={(e) => setUsername(e.target.value)} required />
          </FloatingLabel>
          <FloatingLabel className="mb-3" label="Password">
            <Form.Control type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
          </FloatingLabel>
          <button type="submit" className="btn btn-warning w-100" disabled={loading}>
            {loading ? "Registrazione in corso..." : "Registrati"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Registrati;
