import { useEffect, useState } from "react";
import { Card, Button, Alert } from "react-bootstrap";

const PromozioniLineaFissa = () => {
  const [promozione, setPromozione] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Recupera il token dal localStorage
    const token = localStorage.getItem("authToken");

    if (!token) {
      console.error("Nessun token trovato");
      setLoading(false);
      return;
    }

    fetch("http://localhost:9090/promozioniLineaFissa", {
      headers: {
        Authorization: `Bearer ${token}`,
        Accept: "application/json",
      },
    })
      .then((response) => response.json())
      .then((data) => {
        setPromozione(data);
        setLoading(false);
        console.log(data);
      })
      .catch((error) => {
        console.error("Errore nel recupero dei dati:", error);
        setLoading(false);
      });
  }, []);

  return (
    <>
      <Alert variant="danger my-4 mx-5">
        <Alert.Heading className="text-center fw-bold">ATTENZIONE</Alert.Heading>
        <p className="text-center">
          Passando a FlySim da qualsiasi altro operatore puoi beneficiare di un 10% di sconto e nessun costo di attivazione per una nuova scheda eSim.
        </p>
      </Alert>
      <div>
        {loading ? (
          <p className="text-center">Caricamento...</p>
        ) : promozione ? (
          <Card className="text-center my-5 mx-5 shadow-lg">
            <Card.Header className="fw-bold fs-3">{promozione.nome}</Card.Header>
            <Card.Body>
              <Card.Text>{promozione.descrizione}</Card.Text>
              <p>
                <strong>Giga:</strong> {promozione.giga} | <strong>Minuti:</strong> {promozione.minuti}
              </p>
              <p>
                <strong>Prezzo:</strong> â‚¬{promozione.prezzo}
              </p>
              <Button variant="primary">Acquista</Button>
            </Card.Body>
            <Card.Footer className="text-muted bg-warning">{promozione.promozioniLineaFissaEnum}</Card.Footer>
          </Card>
        ) : (
          <p className="text-center">Errore nel caricamento dei dati.</p>
        )}
      </div>
    </>
  );
};

export default PromozioniLineaFissa;
