import React, { useState, useEffect } from "react";

const AdminPage = () => {
  const [promozioni, setPromozioni] = useState([]);
  const [newPromozione, setNewPromozione] = useState({
    nome: "",
    descrizione: "",
    minuti: 0,
    giga: 0,
    prezzo: 0.1,
    messaggi: 0,
    promozioniEnum: "ATTIVA",
  });
  const [isAdmin, setIsAdmin] = useState(false);
  const [loading, setLoading] = useState(true);
  const [confirmAdd, setConfirmAdd] = useState(false); // Stato per confermare l'aggiunta

  // Funzione per verificare se l'utente è admin
  useEffect(() => {
    const token = localStorage.getItem("authToken");
    if (token) {
      const decodedToken = JSON.parse(atob(token.split(".")[1])); // Decodifica il token
      const roles = decodedToken.roles;
      setIsAdmin(roles.includes("ROLE_ADMIN")); // Verifica se l'utente è un admin
    }
    setLoading(false);
  }, []);

  // Funzione per recuperare le promozioni dal backend
  const fetchPromozioni = async () => {
    try {
      const token = localStorage.getItem("authToken");
      const response = await fetch("http://localhost:9090/api/promozioni", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error("Errore nel recupero delle promozioni");
      }

      const data = await response.json();
      setPromozioni(data);
    } catch (error) {
      console.error("Errore nel recupero delle promozioni:", error);
    }
  };

  // Funzione per aggiungere una nuova promozione
  const addPromozione = async () => {
    try {
      const token = localStorage.getItem("authToken");
      const response = await fetch("http://localhost:9090/api/admin/promozioni/add", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(newPromozione),
      });

      if (!response.ok) {
        throw new Error("Errore nell'aggiungere la promozione");
      }

      setConfirmAdd(false); // Reset della conferma
      setNewPromozione({
        nome: "",
        descrizione: "",
        minuti: 0,
        giga: 0,
        prezzo: 0.1,
        messaggi: 0,
        promozioniEnum: "ATTIVA",
      }); // Reset dei campi
      fetchPromozioni(); // Ricarica le promozioni
    } catch (error) {
      console.error("Errore nell'aggiungere la promozione:", error);
    }
  };

  // Funzione per gestire la modifica dei valori
  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewPromozione((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  // Funzione per annullare la conferma
  const cancelAdd = () => {
    setConfirmAdd(false);
  };

  // Funzione per cancellare una promozione
  const deletePromozione = async (id) => {
    const confirmDelete = window.confirm("Sei sicuro di voler eliminare questa promozione?");
    if (confirmDelete) {
      try {
        const token = localStorage.getItem("authToken");
        const response = await fetch(`http://localhost:9090/api/admin/promozioni/delete/${id}`, {
          method: "DELETE",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          throw new Error("Errore nell'eliminare la promozione");
        }

        fetchPromozioni(); // Ricarica le promozioni dopo l'eliminazione
      } catch (error) {
        console.error("Errore nell'eliminare la promozione:", error);
      }
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div>
      {isAdmin ? (
        <div>
          <h2>Gestione Promozioni</h2>

          {/* Form per aggiungere la promozione */}
          {!confirmAdd ? (
            <div>
              <input type="text" name="nome" value={newPromozione.nome} onChange={handleChange} placeholder="Nome promozione" />
              <input type="text" name="descrizione" value={newPromozione.descrizione} onChange={handleChange} placeholder="Descrizione" />
              <input type="number" name="minuti" value={newPromozione.minuti} onChange={handleChange} placeholder="Minuti" />
              <input type="number" name="giga" value={newPromozione.giga} onChange={handleChange} placeholder="Giga" />
              <input type="number" name="prezzo" value={newPromozione.prezzo} onChange={handleChange} placeholder="Prezzo" />
              <input type="number" name="messaggi" value={newPromozione.messaggi} onChange={handleChange} placeholder="Messaggi" />
              <select name="promozioniEnum" value={newPromozione.promozioniEnum} onChange={handleChange}>
                <option value="ATTIVA">Attiva</option>
                <option value="INATTIVA">Inattiva</option>
              </select>
              <button onClick={() => setConfirmAdd(true)}>Conferma Aggiunta</button>
            </div>
          ) : (
            <div>
              <h3>Conferma Promozione</h3>
              <p>Nome: {newPromozione.nome}</p>
              <p>Descrizione: {newPromozione.descrizione}</p>
              <p>Minuti: {newPromozione.minuti}</p>
              <p>Giga: {newPromozione.giga}</p>
              <p>Prezzo: {newPromozione.prezzo}</p>
              <p>Messaggi: {newPromozione.messaggi}</p>
              <p>Stato: {newPromozione.promozioniEnum}</p>
              <button onClick={addPromozione}>Aggiungi</button>
              <button onClick={cancelAdd}>Annulla</button>
            </div>
          )}

          {/* Visualizza tutte le promozioni */}
          <ul>
            {promozioni.map((promozione) => (
              <li key={promozione.id}>
                <div>
                  <p>
                    <strong>Nome:</strong> {promozione.nome}
                  </p>
                  <p>
                    <strong>Descrizione:</strong> {promozione.descrizione}
                  </p>
                  <p>
                    <strong>Minuti:</strong> {promozione.minuti}
                  </p>
                  <p>
                    <strong>Giga:</strong> {promozione.giga}
                  </p>
                  <p>
                    <strong>Prezzo:</strong> {promozione.prezzo}
                  </p>
                  <p>
                    <strong>Messaggi:</strong> {promozione.messaggi}
                  </p>
                  <p>
                    <strong>Stato:</strong> {promozione.promozioniEnum}
                  </p>

                  <button>Modifica</button>
                  {/* Bottone per cancellare la promozione */}
                  <button onClick={() => deletePromozione(promozione.id)}>Elimina</button>
                </div>
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <div>
          <h2>Accesso non autorizzato</h2>
          <p>Solo gli amministratori possono gestire le promozioni.</p>
        </div>
      )}
    </div>
  );
};

export default AdminPage;
