import React, { useState, useEffect, useRef } from "react";
import { Button, ListGroup, Form, InputGroup, Overlay, Popover } from "react-bootstrap";
import { FaComment, FaTimes, FaPaperPlane } from "react-icons/fa";

const ChatPopup = () => {
  const [showChat, setShowChat] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [currentOptions, setCurrentOptions] = useState([]);
  const target = useRef(null);
  const messagesEndRef = useRef(null);

  // Database delle risposte
  const chatResponses = {
    initial: {
      text: "Ciao! Sono il tuo assistente virtuale. Seleziona un'opzione e verrari rendirizzato all apagina richiesta :",
      options: ["1. Informazioni su prodotti", "2. Assistenza tecnica", "3. Parlare con un operatore"],
    },
    1: {
      text: "Ecco le nostre categorie prodotti:",
      options: ["1.1 Smartphone", "1.2 Linee fisse", "1.3 Linea Mobile", "0. Torna al menù principale"],
    },
    1: {
      text: "Abbiamo i migliori smartphone delle marche Apple, Samsung e Google. Vuoi:",
      options: ["1.1.1 Vedere i modelli", "0. Torna al menù principale"],
    },
    1: {
      text: "Le nostre linee fisse offrono:",
      options: ["1.2.1 Internet veloce", "1.2.2 Telefonia illimitata", "0. Torna al menù principale"],
    },
    1: {
      text: "Promozioni attive questo mese:",
      options: ["1.3.1 Sconto 20% sul secondo dispositivo", "1.3.2 Giga illimitati a 9.99€/mese", "0. Torna al menù principale"],
    },
    2: {
      text: "Assistenza tecnica:",
      options: ["2.1 Problemi di connessione", "2.2 Configurazione dispositivo", "0. Torna al menù principale"],
    },
    3: {
      text: "Un operatore sarà con te a breve. Nel frattempo, vuoi:",
      options: ["3.1 Lasciare un recapito", "3.2 Descrivere il problema", "0. Torna al menù principale"],
    },
    default: {
      text: "Non ho capito la tua richiesta. Seleziona un'opzione:",
      options: ["1. Informazioni su prodotti", "2. Assistenza tecnica", "3. Parlare con un operatore"],
    },
  };

  useEffect(() => {
    // Messaggio iniziale
    setMessages([chatResponses.initial]);
    setCurrentOptions(chatResponses.initial.options);
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages, showChat]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  const handleSend = () => {
    if (!inputValue.trim()) return;

    // Aggiungi messaggio utente
    const userMessage = { text: inputValue, isUser: true };
    setMessages((prev) => [...prev, userMessage]);
    setInputValue("");

    // Simula ritardo risposta bot
    setTimeout(() => {
      let response = chatResponses[inputValue] || chatResponses.default;

      setMessages((prev) => [
        ...prev,
        {
          text: response.text,
          isUser: false,
          options: response.options,
        },
      ]);
      setCurrentOptions(response.options || []);
    }, 500);
  };

  const handleOptionSelect = (option) => {
    const number = option.split(".")[0].trim();
    setInputValue(number);
    handleSend();
  };

  return (
    <>
      {/* Pulsante chat fisso */}
      <Button
        ref={target}
        variant="primary"
        onClick={() => setShowChat(!showChat)}
        style={{
          position: "fixed",
          bottom: "20px",
          right: "20px",
          borderRadius: "50%",
          width: "60px",
          height: "60px",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          zIndex: 1000,
        }}
      >
        {showChat ? <FaTimes size={20} /> : <FaComment size={20} />}
      </Button>

      {/* Popup della chat */}
      <Overlay show={showChat} target={target.current} placement="top-end" container={document.body} rootClose onHide={() => setShowChat(false)}>
        <Popover
          id="chat-popover"
          style={{
            width: "350px",
            maxWidth: "350px",
            height: "500px",
            display: "flex",
            flexDirection: "column",
          }}
        >
          <Popover.Header as="h3" className="d-flex justify-content-between align-items-center">
            Assistenza Clienti
            <Button variant="link" onClick={() => setShowChat(false)} style={{ padding: 0 }}>
              <FaTimes />
            </Button>
          </Popover.Header>
          <Popover.Body style={{ flex: 1, overflowY: "auto", padding: "10px" }}>
            <div className="chat-messages">
              {messages.map((msg, index) => (
                <div key={index} className={`mb-2 ${msg.isUser ? "text-end" : "text-start"}`}>
                  <div className={`d-inline-block p-2 rounded ${msg.isUser ? "bg-primary text-white" : "bg-light"}`} style={{ maxWidth: "80%" }}>
                    {msg.text}
                  </div>

                  {msg.options && !msg.isUser && (
                    <ListGroup className="mt-2">
                      {msg.options.map((opt, i) => (
                        <ListGroup.Item key={i} action onClick={() => handleOptionSelect(opt)} className="option-item">
                          {opt}
                        </ListGroup.Item>
                      ))}
                    </ListGroup>
                  )}
                </div>
              ))}
              <div ref={messagesEndRef} />
            </div>
          </Popover.Body>
          <div className="p-2 border-top">
            <InputGroup>
              <Form.Control
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                placeholder="Scrivi il numero dell'opzione..."
                onKeyPress={(e) => e.key === "Enter" && handleSend()}
              />
              <Button variant="primary" onClick={handleSend}>
                <FaPaperPlane />
              </Button>
            </InputGroup>
          </div>
        </Popover>
      </Overlay>
    </>
  );
};

export default ChatPopup;
