import React, { useState, useEffect, useRef } from "react";
import { Button, ListGroup, Form, InputGroup, Overlay, Popover } from "react-bootstrap";
import { FaComment, FaTimes, FaPaperPlane } from "react-icons/fa";

const ChatPopup = () => {
  const [showChat, setShowChat] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [currentOptions, setCurrentOptions] = useState([]);
  const target = useRef(null);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    // Messaggio iniziale del bot
    setMessages([
      {
        text: "Ciao! Sono il tuo assistente virtuale. Seleziona un'opzione:",
        isUser: false,
        options: ["1. Informazioni su prodotti", "2. Assistenza tecnica", "3. Parlare con un operatore"],
      },
    ]);
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages, showChat]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  const handleSend = async () => {
    if (!inputValue.trim()) return;

    // Aggiungi messaggio utente
    const userMessage = { text: inputValue, isUser: true };
    setMessages((prev) => [...prev, userMessage]);
    setInputValue("");

    try {
      const response = await fetch("http://localhost:9090/api/chat/send", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(inputValue),
        credentials: "include",
      });

      const data = await response.json();

      // Ottieni i nuovi messaggi (GET)
      getMessages();

      setMessages((prev) => [
        ...prev,
        {
          text: data.message,
          isUser: false,
          options: data.options, // Aggiunge le opzioni dalla risposta
        },
      ]);
      setCurrentOptions(data.options || []);
      setTimeout(() => {
        scrollToBottom();
      }, 100);
    } catch (error) {
      console.error("Errore nella chat:", error);
    }
  };

  const handleOptionSelect = (option) => {
    const number = option.split(".")[0].trim();
    setInputValue(number);
    handleSend(); // Invia automaticamente la risposta quando si seleziona un'opzione
  };

  // Funzione per ottenere i messaggi dal server
  const getMessages = async () => {
    try {
      const response = await fetch("http://localhost:9090/api/chat/messages", {
        method: "GET",
        credentials: "include",
      });

      const data = await response.json();

      // Aggiungi i messaggi ottenuti
      setMessages((prev) => [...prev, ...data.messages]);
    } catch (error) {
      console.error("Errore nel recupero dei messaggi:", error);
    }
  };

  return (
    <>
      {/* Pulsante chat fisso */}
      <Button
        ref={target}
        variant="primary"
        onClick={() => setShowChat(!showChat)}
        style={{
          position: "fixed",
          bottom: "20px",
          right: "20px",
          borderRadius: "50%",
          width: "60px",
          height: "60px",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          zIndex: 1000,
        }}
      >
        {showChat ? <FaTimes size={20} /> : <FaComment size={20} />}
      </Button>

      {/* Popup della chat */}
      <Overlay show={showChat} target={target.current} placement="top-end" container={document.body} rootClose onHide={() => setShowChat(false)}>
        <Popover
          id="chat-popover"
          style={{
            width: "350px",
            maxWidth: "350px",
            height: "500px",
            display: "flex",
            flexDirection: "column",
          }}
        >
          <Popover.Header as="h3" className="d-flex justify-content-between align-items-center">
            Assistenza Clienti
            <Button variant="link" onClick={() => setShowChat(false)} style={{ padding: 0 }}>
              <FaTimes />
            </Button>
          </Popover.Header>
          <Popover.Body style={{ flex: 1, overflowY: "auto", padding: "10px" }}>
            <div className="chat-messages">
              {messages.map((msg, index) => (
                <div key={index} className={`mb-2 ${msg.isUser ? "text-end" : "text-start"}`}>
                  <div className={`d-inline-block p-2 rounded ${msg.isUser ? "bg-primary text-white" : "bg-light"}`}>{msg.text}</div>

                  {msg.options && !msg.isUser && (
                    <ListGroup className="mt-2">
                      {msg.options.map((opt, i) => (
                        <ListGroup.Item key={i} action onClick={() => handleOptionSelect(opt)} className="option-item">
                          {opt}
                        </ListGroup.Item>
                      ))}
                    </ListGroup>
                  )}
                </div>
              ))}
              <div ref={messagesEndRef} />
            </div>
          </Popover.Body>
          <div className="p-2 border-top">
            <InputGroup>
              <Form.Control
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                placeholder="Scrivi qui..."
                onKeyPress={(e) => e.key === "Enter" && handleSend()}
              />
              <Button variant="primary" onClick={handleSend}>
                <FaPaperPlane />
              </Button>
            </InputGroup>
          </div>
        </Popover>
      </Overlay>
    </>
  );
};

export default ChatPopup;
