import React, { useState } from "react";

const Registrati = () => {
  const [error, setError] = useState(null); // Stato per gestire errori
  const [loading, setLoading] = useState(false); // Stato per gestire lo stato di caricamento
  const [firstName, setFirstName] = useState(""); // Stato per il primo nome
  const [lastName, setLastName] = useState(""); // Stato per il cognome
  const [email, setEmail] = useState(""); // Stato per l'email
  const [password, setPassword] = useState("");
  const [username, setUsername] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Verifica che tutti i campi siano riempiti
    if (!username || !password || !firstName || !lastName || !email) {
      setError("Tutti i campi sono obbligatori.");
      return;
    }

    setLoading(true); // Impostiamo lo stato di caricamento

    try {
      const response = await fetch("http://localhost:8000/api/auth/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          username,
          password,
          firstName,
          lastName,
          email,
        }),
      });

      if (!response.ok) {
        throw new Error("Errore nella registrazione, riprova.");
      }

      const data = await response.json();
      console.log("Registrazione avvenuta con successo", data);
      console.log(response);

      // Reset dei campi di input
      setUsername("");
      setPassword("");
      setFirstName("");
      setLastName("");
      setEmail("");
    } catch (error) {
      setError(error.message); // Gestione degli errori
    } finally {
      setLoading(false); // Disattiviamo lo stato di caricamento
    }
  };

  return (
    <div>
      <h2>Registrati</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="firstName">Nome:</label>
          <input
            type="text"
            id="firstName"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)} // Gestione input per firstName
          />
        </div>
        <div>
          <label htmlFor="lastName">Cognome:</label>
          <input
            type="text"
            id="lastName"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)} // Gestione input per lastName
          />
        </div>
        <div>
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)} // Gestione input per email
          />
        </div>
        <div>
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)} // Gestione input per username
          />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)} // Gestione input per password
          />
        </div>

        <button type="submit" disabled={loading}>
          {loading ? "Registrazione in corso..." : "Registrati"}
        </button>
      </form>
      {error && <p style={{ color: "red" }}>{error}</p>} {/* Visualizzazione dell'errore */}
    </div>
  );
};

export default Registrati;
